import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { CartContext } from '../context/CartContext';
import './Navbar.css';

function Navbar() {
  const { cart } = useContext(CartContext);
  const navigate = useNavigate();

  const handleNotifications = () => {
    alert('No new notifications.'); // Placeholder for notifications
  };

  return (
    <nav className="navbar">
      <div className="navbar-brand">
        <h2>Praso</h2>
      </div>
      <ul className="navbar-links">
        <li><a href="/" className={window.location.pathname === '/' ? 'active' : ''}>Home</a></li>
        <li><a href="/shop" className={window.location.pathname === '/shop' ? 'active' : ''}>Shop</a></li>
        <li><a href="/about" className={window.location.pathname === '/about' ? 'active' : ''}>About Us</a></li>
        <li><a href="/contact" className={window.location.pathname === '/contact' ? 'active' : ''}>Contact</a></li>
      </ul>
      <div className="navbar-icons">
        <span className="icon user-icon" onClick={() => navigate('/login')} title="Login">
          ðŸ‘¤
        </span>
        <span className="icon bell-icon" onClick={handleNotifications} title="Notifications">
          ðŸ””
        </span>
        <span className="icon cart-icon" onClick={() => navigate('/cart')} title="Cart">
          ðŸ›’ {cart.length > 0 && <span className="cart-count">{cart.length}</span>}
        </span>
      </div>
    </nav>
  );
}

export default Navbar;
